INSERT INTO `config_info` VALUES (1, 'cloud-webmagic-dev.yaml', 'DEFAULT_GROUP', 'server:\n  port: 9001\nspring:\n  datasource:\n    url: jdbc:mysql://localhost:3306/cloud-webmagic?characterEncoding=UTF-8&useUnicode=true&useSSL=false&tinyInt1isBit=false&allowPublicKeyRetrieval=true&serverTimezone=Asia/Shanghai\n    username: root\n    password: 123456\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      # 连接池的配置信息\n      # 初始化大小，最小，最大\n      initial-size: 5\n      min-idle: 5\n      maxActive: 20\n      # 配置获取连接等待超时的时间\n      maxWait: 60000\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\n      timeBetweenEvictionRunsMillis: 60000\n      # 配置一个连接在池中最小生存的时间，单位是毫秒\n      minEvictableIdleTimeMillis: 300000\n      validationQuery: SELECT 1 FROM sys_user\n      testWhileIdle: true\n      testOnBorrow: false\n      testOnReturn: false\n      # 打开PSCache，并且指定每个连接上PSCache的大小\n      poolPreparedStatements: true\n      maxPoolPreparedStatementPerConnectionSize: 20\n      # 配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\n      filters: stat,wall,slf4j\n      # 通过connectProperties属性来打开mergeSql功能；慢SQL记录\n      connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\n      web-stat-filter:\n        enabled: true\n      stat-view-servlet:\n        enabled: true\n        login-username: admin\n        login-password: 123456\n  redis:\n    host: 127.0.0.1\n    port: 6379\n    password:\n    lettuce:\n      pool:\n        # 连接池最大连接数(使用负值表示没有限制) 默认为8\n        max-active: 8\n        # 连接池最大阻塞等待时间(使用负值表示没有限制) 默认为-1\n        max-wait: -1ms\n        # 连接池中的最大空闲连接 默认为8\n        max-idle: 8\n        # 连接池中的最小空闲连接 默认为 0\n        min-idle: 0\n        statViewServlet:\n          enabled: true\n  # kafka:\n    #kafka地址，可以指定多个\n    # bootstrap-servers: 127.0.0.1:9092\n    # consumer:\n      #指定group_id\n      # group-id: group_id\n      #earliest：当各分区下有已提交的 offset 时，从提交的 offset 开始消费；无提交的 offset时，从头开始消费；\n      #latest：当各分区下有已提交的 offset 时，从提交的 offset 开始消费；无提交的 offset 时，消费新产生的该分区下的数据；\n    #   auto-offset-reset: earliest\n    #   key-serializer: org.apache.kafka.common.serialization.StringSerializer\n    #   value-serializer: org.apache.kafka.common.serialization.StringSerializer\n    # producer:\n    #   key-serializer: org.apache.kafka.common.serialization.StringSerializer\n    #   value-serializer: org.apache.kafka.common.serialization.StringSerializer\n#mybatis plus 设置\nmybatis-plus:\n  mapper-locations: classpath*:com/watercloud/webmagic/mapper/*.xml\n  global-config:\n    banner: true # MP3.0自带的banner\n    db-config:\n      #主键类型  0:\"数据库ID自增\",1:\"该类型为未设置主键类型\", 2:\"用户输入ID\",3:\"全局唯一ID (数字类型唯一ID)\", 4:\"全局唯一ID UUID\",5:\"字符串全局唯一ID (idWorker 的字符串表示)\";\n# id-type: ASSIGN_ID\n# 默认数据库表下划线命名\n      table-underline: true \n# 这个配置会将执行的sql打印出来，在开发或测试的时候可以用      \n  configuration:\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl \n# 返回类型为Map,显示null对应的字段    \n    call-setters-on-nulls: true \n# 指定需要扫描的entity包     \n  type-aliases-package: com.watercloud.webmagic.entity  \n#以下为自定义配置\nJWTConfig:\n  ISSUER: watercloud\n  SECRET: 01ea098224c7d0d2077c14b9a3a1ed16\n  EXPIRE_TIME: 1800000\nShiroConfig:\n  AnonUrls: /druid/**,/**/*.js,/**/*.css,/**/*.html,/**/*.svg,/**/*.pdf,/**/*.jpg,/**/*.png,/**/*.ico\n  redisExpire: 20\nredission:\n  address: redis://127.0.0.1:6379\nredisShiro:\n  address: 127.0.0.1:6379   ', '0b7d796f2b3e516ccabf44106f4f28bc', '2021-12-24 15:30:16', '2022-04-29 17:21:44', 'nacos', '172.17.0.1', '', '', 'cloud-webmagic的配置文件', '', '', 'yaml', '');
